// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./IERC20.sol";

contract DiningHall {
    address public admin;
    IERC20 public token;

    event BuyMeal(
        address indexed buyer,
        uint256 ethAmount,
        uint256 tokenAmount
    );

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin calls");
        _;
    }

    constructor(address _tokenAddress) {
        admin = msg.sender;
        token = IERC20(_tokenAddress);
    }

    function getUserTokenBalance(
        address account
    ) public view returns (uint256) {
        // Kullancnn token bakiyesini almak iin bu fonksiyonu kullanabilirsiniz
        return token.balanceOf(account);
    }

    function buyMealWithTokens(uint256 _tokenAmount) external {
        require(_tokenAmount > 0, "Invalid token amount");

        // Kullancnn yeterli miktarda tokena sahip olup olmadn kontrol et
        require(
            getUserTokenBalance(msg.sender) >= _tokenAmount,
            "Insufficient balance"
        );

        // Kullancnn tokenlar bu akll kontrata aktar
        require(
            token.transferFrom(msg.sender, address(this), _tokenAmount),
            "Token transfer failed"
        );

        // Burada yemek alma ilemleri gerekletirilebilir
        // rnein, belirli bir miktarda yemek verilebilir

        emit BuyMeal(msg.sender, 0, _tokenAmount); // Burada ethAmount 0 olarak belirtilmitir, nk ETH alm olmadan sadece token ile ilem gerekleiyor.
    }

    // Dier fonksiyonlar...
}
